<?php

namespace Hris\UserBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * GroupRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class GroupRepository extends EntityRepository
{
    /**
     * Returns usersGroups with matching name
     * @param $name
     * @return \Doctrine\Common\Collections\Collection
     */
    public function getSearchedUserGroups($name)
    {
        $queryBuilder = $this->getEntityManager()->createQueryBuilder();
        $result = $queryBuilder->select('userGroup')
            ->from('HrisUserBundle:Group','userGroup')
            ->where('lower(userGroup.name) like :name')
            ->setParameters(array(
                    'name'=>"%$name%"
                )
            )->getQuery()->getResult();

        return $result;
    }
}
